#!/usr/bin/env python3# -*- coding: utf-8 -*-"""Created on Wed Oct 16 13:21:36 2024@author: matthewyakir"""import pandas as pdimport kaggleimport zipfileimport osif __name__ == '__main__':    # Set the download path where you want to save the dataset    download_path = os.getcwd()    # Download the dataset using Kaggle API    kaggle.api.dataset_download_files('lainguyn123/student-performance-factors',                                       path=download_path, unzip=True)    # Path to the downloaded zip file    zip_path = os.path.join(download_path, 'student-performance-factors.zip')    #print(zip_path)     #input('An')    # Unzip the dataset    with zipfile.ZipFile(zip_path, 'r') as zip_ref:        zip_ref.extractall(download_path)    # Load the CSV file into a pandas DataFrame (adjust file name if necessary)    csv_path = os.path.join(download_path,'StudentPerformanceFactors.csv')    df = pd.read_csv(csv_path)        # Print the first few rows of the DataFrame    print(df.head())        